import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Alert, Button, StyleSheet, Text, View } from 'react-native';
import { WebView } from 'react-native-webview';
import * as imagecapture from 'expo-image-picker';
import * as permission from 'expo-permissions';
import * as camera from 'expo-camera';
import { useState, useEffect } from 'react';

async function loadPermission(){
  Alert.alert("inside function");
  try{
    const {granted} =  await permission.askAsync(permission.CAMERA,permission.AUDIO_RECORDING);
   // const {status} = camera.requestCameraPermissionsAsync()
    if(granted){
      Alert.alert("Permission provided "+granted);
    }else{
      Alert.alert(" Need Permission "+granted);
    }
  }catch(error){
      alert(error)
  }

}
